FightScript:
if (PlayerInfo.Lucky<40)
        {
            return;
        }AddEvent("OnDiceValue", () =>
        {
                ValueDice.OnRoll += result => {
            result.CopyTo(new(result.Value+10,result.Bonus));
            };
        });
AddEvent("OnDiceCheck", () =>
        {

                CheckDice.OnRoll += result => {
 result.CopyTo(new( (result.Value-10),result.Bonus));
            };
        });

if (PlayerInfo.Perceive<40)
        {
            return;
        }AddEvent("StartRound",() => {SetStatus("Self");foreach (var item in Object)
        {
            if (GetStatus.ContainsKey(item))
            {
                item.CurHp = GetStatus[item]["CurHp"];
                item.MaxHp = GetStatus[item]["MaxHp"];
            }
            else
            {
                GetStatus[item] = new Dictionary<string, int>
                {
                    { "CurHp", item.CurHp },
                    { "MaxHp", item.MaxHp },
                };
            }
            AddBuff("buff_impregnable","1");
        };});

if (PlayerInfo.SpecialVars.ContainsKey("blesing_35") ||PlayerInfo.enemylevel==4)
        {
            return;
        }
        ChangeDynamicVar("liveCount", "1");
        AddEvent("Dead",()=> {
            if (!PlayerInfo.SpecialVars.ContainsKey("blesing_35"))
            {
                if (Self.dynamicVariables["liveCount"]==0f)
                {
                    PlayerInfo.SpecialVars["blesing_35"] = "1";
                EscapeFight();
                }

            }
        });

AddEvent("FightStart", () => {
            Vars["ThisCount"] = (int.Parse(Vars.GetValueOrDefault("ThisCount", "0")) + 1).ToString();
            if (int.Parse(Vars["ThisCount"]) < 4)
            {
                PlayerInfo.MaxHp -= 4;
            }
            AddEvent("Escape", () =>
            {
                if (int.Parse(Vars["ThisCount"]) >=3)
                {
                    for (int i = 0; i < 3; i++)
                    {
                        PlayerInfo.AddBless("blessing_6");
                    PlayerInfo.AddBless("blessing_20");
                    }

                    PlayerInfo.RemoveBless("blessing_36");
                }
            });
            AddEvent("Win",()=> {
                if (int.Parse(Vars["ThisCount"]) >=3)
                {
                    for (int i = 0; i < 3; i++)
                    {
                        PlayerInfo.AddBless("blessing_6");
                    PlayerInfo.AddBless("blessing_20");
                    }

                    PlayerInfo.RemoveBless("blessing_36");
                }
            });
             });

