UseScript:
SetStatus("Target");
        foreach (var item in Object)
        {
            Self.dynamicVariables=item.dynamicVariables;
            item.ResetDamageStatus();
        }

SetStatus("Self");
        ChangeDefence(def());
        SetStatus("AllTarget");
        foreach (var status in Object)
        {
            if (status.buffBarUI != null)
            {
                var buff = status.buffBarUI.GetBuff("buff_burn");
                if (buff != null)
                {
                    Vars["CorDef"]=(int.Parse(Vars["CorDef"]) + (buff.buffConfig.Level)).ToString();
                } 
           }
        }

SetStatus("AllTarget");
        int k = int.Parse(PlayerInfo.GetDiff()) > 3 ? 3 : 2;
        for (int i = 0; i < k; i++)
        {
            AddCard("cursecard_3");
        }
        Vars["ThisCount"] = (int.Parse(Vars["ThisCount"]) + 1).ToString();

SetStatus("AllFriend");
        foreach (var item in Object)
        {
            item.ToughOrigin += 1;
        }
        SetStatus("Self");
        AddBuff("buff_extraordinary",INF,"20");
